import axios from "axios"
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function ListPost() {

    const [posts, setPosts] = useState([]);
    useEffect(() => {
        getPosts();
    }, []);

    function getPosts() {
        axios.get('http://localhost/react-crud-php-api-mysql/api/posts/').then(function(response) {
            console.log(response.data);
            setUsers(response.data);
        });
    }

    const deletePost = (id) => {
        axios.delete(`http://localhost/react-crud-php-api-mysql/api/post/${id}/delete`).then(function(response){
            console.log(response.data);
            getUsers();
        });
    }
    return (
        <div>
            <h1>List Users</h1>
            <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Mobile</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((post, key) =>
                        <tr key={key}>
                            <td>{post.id}</td>
                            <td>{post.title}</td>
                            <td>{post.slug}</td>
                            <td>{post.content}</td>
                            <td>
                                <Link to={`post/${post.id}/edit`} style={{marginRight: "10px"}}>Edit</Link>
                                <button onClick={() => deletePost(post.id)}>Delete</button>
                            </td>
                        </tr>
                    )}

                </tbody>
            </table>
        </div>
    )
}
