import axios from "axios"
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function ListPost() {

    const [posts, setPosts] = useState([]);
    const [q, setQ] = useState("");

    useEffect(() => {
        getPosts();
    }, []);

    function getPosts() {
        axios.get('http://localhost/react-crud-php-api-mysql/api/posts/').then(function(response) {
            console.log(response.data);
            setPosts(response.data);
        });
    }

    const filter = (e) => {
        const keyword = e.target.value;
    }
    const deletePost = (id) => {
        axios.delete(`http://localhost/react-crud-php-api-mysql/api/post/${id}/delete`).then(function(response){
            console.log(response.data);
            getPosts();
        });
    }
    return (






        <div>
            <h1>List Posts</h1>
                    <div className="search-wrapper">
                        <label htmlFor="search-form">
                            <input
                                type="search"
                                name="search-form"
                                id="search-form"
                                className="search-input"
                                placeholder="Search for..."
                                value={q}
                                /*
                                // set the value of our useState q
                                //  anytime the user types in the search box
                                */
                                onChange={(e) => setQ(e.target.value)}
                            />
                            <span className="sr-only">Search here</span>
                        </label>
                    </div>


                    <table class="table">                
                    <thead class="thead-dark">
                    <tr>
                        <th>#</th>
                        <th>Title</th>
                        <th>Slug</th>
                        <th>Content</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {posts.map((post, key) =>
                        <tr key={key}>
                            <td>{post.id}</td>
                            <td>{post.title}</td>
                            <td>{post.slug}</td>
                            <td>{post.content}</td>
                            <td>
                                <Link className="btn btn-primary" to={`post/${post.id}/edit`} style={{marginRight: "10px"}}>Edit</Link>
                                <button className="btn btn-danger" onClick={() => deletePost(post.id)}>Delete</button>
                            </td>
                        </tr>
                    )}

                </tbody>
            </table>
        </div>
    )
}
